import React, { useState } from "react";
import { Row, Col, Button, Input, ListGroupItem } from "reactstrap";
import Select from "react-select";
import { teamColours } from "../../utils/constants";
import { useRecoilValue, useRecoilState } from "recoil";
import { teamDictionaryState } from "../../store/selectors";
import { playerListState } from "../../store/atoms";

export default function Players({input, setInput}) {
  const [playerList, setPlayerList] = useRecoilState(playerListState);
  const teamDictionary = useRecoilValue(teamDictionaryState);
  // const [playerName, setPlayerName] = useState("");
  const [playerTeam, setPlayerTeam] = useState();

  const addPlayer = () => {
    const newPlayers = [
      ...playerList,
      {
        id: playerList.length,
        name: playerName,
        team: playerTeam
      }
    ];

    setPlayerList(newPlayers);
    setPlayerName("");
  };

  return (
    <>
      <Row className="mt-3">
        <Col md="5">
          <Input
            type="text"
            placeholder="Add player..."
            value={input}
            onChange={e => setInput(e.target.value)}
          />
        </Col>
        <Col md="5">
          <Select
            options={teamDictionary}
            onChange={data => setPlayerTeam(data.value)}
          />
        </Col>
        <Button color="primary" type="submit" onClick={addPlayer}>
          Add
        </Button>
      </Row>
      <PlayerList />
    </>
  );
}

function PlayerList() {
  const players = useRecoilValue(playerListState);

  return (
    <div className="mt-3">
      {players.map(player => (
        <ListGroupItem color={teamColours[player.team]}>
          {player.name}
        </ListGroupItem>
      ))}
    </div>
  );
}
